#intermediary methods-----------------
#-----to let the user choose what she wants
def user_interface
  puts "---------------------------------------------------"
  puts "----Bonjour, ceci est un programme qui analyse ----"
  puts "----une liste d'username Twitter des journalistes--"
  puts "---------------------------------------------------"
  puts "----Quelle donnée vous intéresse-elle ？-----------"
  puts "---------------------------------------------------"
  puts "----Tapez un des choix proposés dessous------------"
  puts "----1. Le nombre de journalistes dans la liste"
  puts "----2. Le nombre d'usernames contenant un numéro"
  puts "----3. Le nombre d'usernames contenant la combinaison"
  puts "      des lettres 'aude' à la suite"
  puts "----4. Le nombre d'usernames commençant par une majuscule"
  puts "----5. Le nombre d'usernames contenant une et une seule majuscule"
  puts "----6. Le nombre d'underscore dans cette liste"
  puts "----7. La position de @epenser dans cette liste"
  puts "----8. Cette liste triée par ordre alphabétique A-Z"
  puts "----9. Les 50 usernames les plus courts de cette liste"
  puts "---------------------------------------------------"
  puts "----T ou t pour tout voir.-------------------------"
  puts "----X ou x Pour terminer le programme--------------"
  puts "Tapez votre réponse :"
  puts ">>  "
  puts "---------------------------------------------------"
  return gets.chomp
end

#-----to see whether a name has one and only one cap
def has_one_cap?(str)
  i = 0
  str.each_char do |char|
     if char == char.upcase
       i += 1
     end
     return i == 1
  end
end

#-----to put stuffs together
def run(input)
  while TRUE
    case input
    when '1'
      puts _1_count_jour
    when '2'
      puts _2_count_num
    when '3'
      puts _3_count_aude
    when '4'
      puts _4_count_start_cap
    when '5'
      puts _5_count_one_cap
    when "6"
      puts _6_count_underscore
    when "7"
      puts _7_position("@epenser")
    when '8'
      _8_sort
    when '9'
      _9_50short
    when 'x' || 'X'
      puts "Au revoir"
      exit!
    when 't' || 'T'
      puts _1_count_jour
      puts _2_count_num
      puts _3_count_aude
      puts _4_count_start_cap
      puts _5_count_one_cap
      puts _6_count_underscore
      puts _7_position("@epenser")
      _8_sort
      _9_50short
      puts"------------------------------------"
      puts"Vous avez tout vu. Au revoir"
      exit!
    end
    puts "-------------------------------------"
    puts "Voulez vous voir d'autres choses ?"
    puts '>> '
    input = gets.chomp
  end
end

#-----to run prog
def perform
  run(user_interface)
end

#methods that really answers those questions-----
# 1.pour compter le nombre de journalites
def _1_count_jour
  puts "La liste contient #{$list.length} journalistes."
end

# 2.pour compter le nb d'username avec chiffre
def _2_count_num
  i=0
  $list.each do |item|
    if item.count("0-9") > 0
      i+=1
    end
  end
  "Cette liste contient #{i} usernames ayant au moins un chiffre."
end

# 3.count usernames having 'aude' lettre combo, case insensitive
def _3_count_aude
  i = 0
  $list.each do |item|
    if item.downcase.include? "aude"
      i+=1
    end
  end
  "Cette liste contient #{i} usernames ayant la conbinaison 'aude' min ou maj."
end

# 4.count usernames that starts with cap letter
def _4_count_start_cap
  i=0
  $list.each do |item|
    if item[1..-1][0..0] =~ /[A-Z]/
      i+=1
    end
  end
  "Cette liste contient #{i} usernames commençant par une majucsule."
end
# 2 and 4 could have been donw together...
# too bad I decided to separate them.....

# 5. count usernames with one and only one cap letter
def _5_count_one_cap
  i = 0
  $list.each do |item|
    if has_one_cap?(item[1..-1])
      i += 1
    end
  end
  "Cette liste contient #{i} usernames avec seulement une majuscule."
end

# 6.pour compter le nombre d'underscore
def _6_count_underscore
  i = 0
  $list.each do |item|
    item.each_char do |character|
      if character == "_"
      i += 1
      end
    end
  end
  return "Cette liste comporte en tout #{i} underscores."
end

# 7.pour la position de @epenser
def _7_position(username)
  i = 1
  $list.each do |item|
    if item != "@epenser"
      i += 1
    else
      break
    end
  end
  "La position de @epense est #{i}"
end

# 8.pour classer les noms par ordre alphabétique
def _8_sort
  print "Voici la liste triée : #{$list.sort}"
  puts ""
end

# 9.the 50 shortest
def _9_50short
  sort_b_length = $list.sort_by {|x| x.length}
  puts "Voici les 50 usernames les plus courts"
  print sort_b_length[0..50]
  puts""
end




